dataSet.query:
SELECT ALLOWED BEGINOFPERIOD(AnswersSliceLast.Visit.Date, DAY) AS Day, AnswersSliceLast.Outlet, AnswersSliceLast.SKU, CASE WHEN AnswersSliceLast.DataType = VALUE(Enum.DataType.Boolean) THEN CASE WHEN AnswersSliceLast.Answer = TRUE THEN 1 WHEN AnswersSliceLast.Answer = FALSE THEN 0 END WHEN AnswersSliceLast.DataType = VALUE(Enum.DataType.Integer) OR AnswersSliceLast.DataType = VALUE(Enum.DataType.Decimal) THEN CASE WHEN AnswersSliceLast.Answer = 0 THEN 0 ELSE 1 END END AS Answer, AnswersSliceLast.Visit.SR AS SR INTO Fact FROM InformationRegister.Answers.SliceLast AS AnswersSliceLast WHERE CASE WHEN &BeginDate = DATETIME(1, 1, 1) THEN TRUE ELSE AnswersSliceLast.Visit.Date >= &BeginDate END AND CASE WHEN &EndDate = DATETIME(1, 1, 1) THEN TRUE ELSE AnswersSliceLast.Visit.Date <= &EndDate END AND CASE WHEN &Question = VALUE(Catalog.Question.EmptyRef) THEN FALSE ELSE AnswersSliceLast.Question = &Question END ;
//////////////////////////////////////////////////////////////////////////////// SELECT DISTINCT Fact.Day INTO Dates FROM Fact AS Fact ;
//////////////////////////////////////////////////////////////////////////////// SELECT ALLOWED AssortmentMatrixOutletsSliceLast.Outlet, AssortmentMatrixSKUsSliceLast.SKU, MAX(AssortmentMatrixSKUsSliceLast.BaseUnitQty) AS BaseUnitQty, AssortmentMatrixPeriods.BeginDate, AssortmentMatrixPeriods.EndDate INTO Plan FROM InformationRegister.AssortmentMatrixSKUs.SliceLast AS AssortmentMatrixSKUsSliceLast FULL JOIN Catalog.AssortmentMatrix.Periods AS AssortmentMatrixPeriods ON AssortmentMatrixSKUsSliceLast.AssortmentMatrix = AssortmentMatrixPeriods.Ref AND AssortmentMatrixSKUsSliceLast.UUID = AssortmentMatrixPeriods.UUID FULL JOIN InformationRegister.AssortmentMatrixOutlets.SliceLast AS AssortmentMatrixOutletsSliceLast ON AssortmentMatrixSKUsSliceLast.AssortmentMatrix = AssortmentMatrixOutletsSliceLast.AssortmentMatrix AND AssortmentMatrixSKUsSliceLast.UUID = AssortmentMatrixOutletsSliceLast.UUID WHERE NOT AssortmentMatrixOutletsSliceLast.OutletStatus = VALUE(Enum.ValueTableRowStatuses.Deleted) AND NOT AssortmentMatrixSKUsSliceLast.SKUStatus = VALUE(Enum.ValueTableRowStatuses.Deleted) AND CASE WHEN &EndDate = DATETIME(1, 1, 1) THEN TRUE ELSE AssortmentMatrixPeriods.BeginDate <= &EndDate END AND CASE WHEN AssortmentMatrixPeriods.EndDate > DATETIME(1, 1, 1) THEN AssortmentMatrixPeriods.EndDate >= &BeginDate ELSE TRUE END
GROUP BY AssortmentMatrixPeriods.EndDate, AssortmentMatrixPeriods.BeginDate, AssortmentMatrixOutletsSliceLast.Outlet, AssortmentMatrixSKUsSliceLast.SKU ;
//////////////////////////////////////////////////////////////////////////////// SELECT Dates.Day, Plan.Outlet, Plan.SKU, MAX(Plan.BaseUnitQty) AS BaseUnitQty, Plan.BeginDate, Plan.EndDate INTO PlanDates FROM Plan AS Plan LEFT JOIN Dates AS Dates ON Plan.BeginDate <= Dates.Day AND (CASE WHEN Plan.EndDate = DATETIME(1, 1, 1) THEN TRUE ELSE Plan.EndDate >= Dates.Day END) WHERE NOT Dates.Day IS NULL
GROUP BY Dates.Day, Plan.Outlet, Plan.SKU, Plan.BeginDate, Plan.EndDate ;
//////////////////////////////////////////////////////////////////////////////// SELECT ALLOWED ISNULL(Fact.Day, PlanDates.Day) AS Day, ISNULL(Fact.Outlet, PlanDates.Outlet) AS Outlet, ISNULL(Fact.SKU, PlanDates.SKU) AS SKU, ISNULL(Fact.Answer, 0) AS Answer, CASE WHEN PlanDates.BaseUnitQty IS NULL THEN 0 ELSE 1 END AS BaseUnitQty, Fact.SR INTO PrePlanFact FROM Fact AS Fact FULL JOIN PlanDates AS PlanDates ON Fact.Outlet = PlanDates.Outlet AND Fact.SKU = PlanDates.SKU AND Fact.Day = PlanDates.Day ;
//////////////////////////////////////////////////////////////////////////////// SELECT ALLOWED PrePlanFact.Day, PrePlanFact.Outlet, PrePlanFact.SKU, PrePlanFact.Answer, PrePlanFact.BaseUnitQty, PrePlanFact.SR INTO PlanFact FROM PrePlanFact AS PrePlanFact WHERE PrePlanFact.Outlet IN (SELECT DISTINCT Fact.Outlet FROM Fact) ;
//////////////////////////////////////////////////////////////////////////////// SELECT ALLOWED PlanFact.Day, PlanFact.Outlet.Distributor, PlanFact.Outlet.Class, PlanFact.Outlet.Type, PlanFact.Outlet, PlanFact.SKU.Brand AS Brand, PlanFact.SKU.Owner AS SKUGroup, PlanFact.SKU, PlanFact.Answer, PlanFact.BaseUnitQty, CASE WHEN PlanFact.BaseUnitQty > 0 AND PlanFact.Answer = 1 THEN 1 ELSE 0 END AS PlanFactQty, TerritoryOutlets.Ref AS Territory, TerritoryOutlets.Ref.Owner AS Region, PlanFact.SR, OutletParameters.Value INTO PlanFactTerritory FROM PlanFact AS PlanFact INNER JOIN Catalog.Territory.Outlets AS TerritoryOutlets ON PlanFact.Outlet = TerritoryOutlets.Outlet LEFT JOIN Catalog.Outlet.Parameters AS OutletParameters ON PlanFact.Outlet = OutletParameters.Ref WHERE OutletParameters.Parameter = &Parameter ;
//////////////////////////////////////////////////////////////////////////////// SELECT DISTINCT Fact.Outlet, Fact.Day INTO FactOutletsDays FROM Fact AS Fact ;
//////////////////////////////////////////////////////////////////////////////// SELECT ALLOWED ISNULL(PlanFactTerritory.Day, MAX(PlanFactTerritory.Day)) AS Day, ISNULL(BEGINOFPERIOD(PlanFactTerritory.Day, MONTH), MAX(BEGINOFPERIOD(PlanFactTerritory.Day, MONTH))) AS Month, ISNULL(CASE WHEN MONTH(PlanFactTerritory.Day) IN (2, 4, 6, 8, 10, 12) THEN BEGINOFPERIOD(DATEADD(PlanFactTerritory.Day, MONTH, -1), MONTH) ELSE BEGINOFPERIOD(PlanFactTerritory.Day, MONTH) END, MAX(CASE WHEN MONTH(PlanFactTerritory.Day) IN (2, 4, 6, 8, 10, 12) THEN BEGINOFPERIOD(DATEADD(PlanFactTerritory.Day, MONTH, -1), MONTH) ELSE BEGINOFPERIOD(PlanFactTerritory.Day, MONTH) END)) AS Month2, ISNULL(BEGINOFPERIOD(PlanFactTerritory.Day, QUARTER), MAX(BEGINOFPERIOD(PlanFactTerritory.Day, QUARTER))) AS Quarter, PlanFactTerritory.OutletDistributor, PlanFactTerritory.OutletClass, PlanFactTerritory.OutletType, PlanFactTerritory.Outlet, PlanFactTerritory.Brand, PlanFactTerritory.SKUGroup, PlanFactTerritory.SKU, MAX(PlanFactTerritory.Answer) AS Answer, MAX(PlanFactTerritory.BaseUnitQty) AS BaseUnitQty, MAX(PlanFactTerritory.PlanFactQty) AS PlanFactQty, PlanFactTerritory.Territory, PlanFactTerritory.Region, MAX(PlanFactTerritory.SR) AS SR, PlanFactTerritory.Value, CASE WHEN FactOutletsDays.Outlet IS NULL THEN 0 ELSE 1 END AS VW FROM PlanFactTerritory AS PlanFactTerritory LEFT JOIN FactOutletsDays AS FactOutletsDays ON PlanFactTerritory.Day = FactOutletsDays.Day AND PlanFactTerritory.Outlet = FactOutletsDays.Outlet
GROUP BY PlanFactTerritory.OutletDistributor, PlanFactTerritory.OutletClass, PlanFactTerritory.OutletType, PlanFactTerritory.Outlet, PlanFactTerritory.Brand, PlanFactTerritory.SKUGroup, PlanFactTerritory.SKU, PlanFactTerritory.Territory, PlanFactTerritory.Region, PlanFactTerritory.Value, PlanFactTerritory.Day, CASE WHEN FactOutletsDays.Outlet IS NULL THEN 0 ELSE 1 END
