dataSet.query:
SELECT ALLOWED BEGINOFPERIOD(AnswersSliceLast.Visit.Date, DAY) AS Date, AnswersSliceLast.Outlet, AnswersSliceLast.SKU, AnswersSliceLast.Answer, AnswersSliceLast.Visit.SR AS SR INTO Fact FROM InformationRegister.Answers.SliceLast(, ) AS AnswersSliceLast WHERE AnswersSliceLast.QuestionAssignment = VALUE(Enum.SKUQuestions.Stock) AND AnswersSliceLast.Visit.Date >= &StartDate AND AnswersSliceLast.Visit.Date <= &EndDate ;
//////////////////////////////////////////////////////////////////////////////// SELECT ALLOWED AssortmentMatrixOutletsSliceLast.Outlet, AssortmentMatrixSKUsSliceLast.SKU, MAX(AssortmentMatrixSKUsSliceLast.BaseUnitQty) AS BaseUnitQty, AssortmentMatrixPeriods.BeginDate, AssortmentMatrixPeriods.EndDate INTO Plan FROM InformationRegister.AssortmentMatrixSKUs.SliceLast AS AssortmentMatrixSKUsSliceLast LEFT JOIN InformationRegister.AssortmentMatrixOutlets.SliceLast AS AssortmentMatrixOutletsSliceLast ON AssortmentMatrixSKUsSliceLast.AssortmentMatrix = AssortmentMatrixOutletsSliceLast.AssortmentMatrix AND AssortmentMatrixSKUsSliceLast.UUID = AssortmentMatrixOutletsSliceLast.UUID LEFT JOIN Catalog.AssortmentMatrix.Periods AS AssortmentMatrixPeriods ON AssortmentMatrixSKUsSliceLast.AssortmentMatrix = AssortmentMatrixPeriods.Ref AND AssortmentMatrixSKUsSliceLast.UUID = AssortmentMatrixPeriods.UUID WHERE AssortmentMatrixSKUsSliceLast.SKUStatus <> VALUE(ENum.ValueTableRowStatuses.Deleted) AND AssortmentMatrixOutletsSliceLast.OutletStatus <> VALUE(ENum.ValueTableRowStatuses.Deleted) AND AssortmentMatrixPeriods.BeginDate <= &EndDate AND CASE WHEN AssortmentMatrixPeriods.EndDate = DATETIME(1, 1, 1) THEN TRUE ELSE AssortmentMatrixPeriods.EndDate >= &StartDate END
GROUP BY AssortmentMatrixSKUsSliceLast.SKU, AssortmentMatrixOutletsSliceLast.Outlet, AssortmentMatrixPeriods.BeginDate, AssortmentMatrixPeriods.EndDate ;
//////////////////////////////////////////////////////////////////////////////// SELECT ALLOWED Fact.Date, Fact.Outlet, Fact.SKU, Fact.Answer AS Fact, Plan.BaseUnitQty AS Plan, Fact.SR INTO PlanFact FROM Fact AS Fact LEFT JOIN Plan AS Plan ON Fact.Date >= Plan.BeginDate AND Fact.SKU = Plan.SKU AND Fact.Outlet = Plan.Outlet AND (Fact.Date <= Plan.EndDate OR Plan.EndDate = DATETIME(1, 1, 1)) ;
//////////////////////////////////////////////////////////////////////////////// SELECT ALLOWED Result.Date, Result.Outlet.Distributor, Result.Outlet.Class, Result.Outlet.Type, Result.Outlet, Result.SKU.Brand AS Brand, Result.SKU.Owner AS SKUGroup, Result.SKU, Result.Fact, Result.Plan, TerritoryOutlets.Ref AS Territory, TerritoryOutlets.Ref.Owner AS Region, OutletParameters.Value, Result.SR INTO PlanFactTerritory FROM PlanFact AS Result INNER JOIN Catalog.Territory.Outlets AS TerritoryOutlets ON Result.Outlet = TerritoryOutlets.Outlet LEFT JOIN Catalog.Outlet.Parameters AS OutletParameters ON Result.Outlet = OutletParameters.Ref {WHERE (OutletParameters.Parameter = &Parameter) AS Field1} ;
//////////////////////////////////////////////////////////////////////////////// SELECT ALLOWED PlanFactTerritory.Date, BEGINOFPERIOD(PlanFactTerritory.Date, MONTH) AS BegOfMonth, CASE WHEN MONTH(PlanFactTerritory.Date) IN (2, 4, 6, 8, 10, 12) THEN BEGINOFPERIOD(DATEADD(PlanFactTerritory.Date, MONTH, -1), MONTH) ELSE BEGINOFPERIOD(PlanFactTerritory.Date, MONTH) END AS BegOf2Month, BEGINOFPERIOD(PlanFactTerritory.Date, QUARTER) AS BegOfQuarter, PlanFactTerritory.OutletDistributor, PlanFactTerritory.OutletClass, PlanFactTerritory.OutletType, PlanFactTerritory.Outlet, PlanFactTerritory.Brand, PlanFactTerritory.SKUGroup, PlanFactTerritory.SKU, ISNULL(PlanFactTerritory.Fact, 0) AS Fact, MAX(ISNULL(PlanFactTerritory.Plan, 0)) AS Plan, PlanFactTerritory.Territory, PlanFactTerritory.Region, PlanFactTerritory.Value, PlanFactTerritory.SR FROM PlanFactTerritory AS PlanFactTerritory
GROUP BY PlanFactTerritory.Date, PlanFactTerritory.OutletDistributor, PlanFactTerritory.OutletClass, PlanFactTerritory.OutletType, PlanFactTerritory.Outlet, PlanFactTerritory.Brand, PlanFactTerritory.SKUGroup, PlanFactTerritory.SKU, PlanFactTerritory.Territory, PlanFactTerritory.Region, PlanFactTerritory.Value, PlanFactTerritory.SR, ISNULL(PlanFactTerritory.Fact, 0)
